import "../styles/banner.css";

export function Banner() {
  return (
    <div className="banner container-md d-flex flex-column align-items-center justify-content-center shadow p-3 mb-5 bg-white rounded">
      <h1 className="text-light" style={{ height: "100px", width: "325px" }}>Helados Artesanales</h1>
      <a href="sabores.html" className="h4 text-light text-decoration-none">
        Lista de Sabores <i className="fas fa-forward arrow"></i>
      </a>
    </div>
  );
}
